import React, { useState } from 'react';
import {ArrowBigLeft, ArrowBigRight} from 'lucide-react'

const images = ["https://cdn.dummyjson.com/recipe-images/10.webp", "https://cdn.dummyjson.com/recipe-images/35.webp", "https://cdn.dummyjson.com/recipe-images/47.webp"];

const MealSlider = () => {
  const [imgIndex, setImgIndex] = useState(0);

  return (
    <div className='max-w-screen-xl'>
      <div className='w-full h-full'>
        <img src={images[imgIndex]} alt='food'/>
        <button><ArrowBigLeft /></button>
        <button><ArrowBigRight /></button>
      </div>
    </div>

  )
}

export default MealSlider;
