import React, { useState } from 'react';
import {ArrowBigLeft, ArrowBigRight} from 'lucide-react';
import '../styles/img-slider.css'

const images = [
  "https://cdn.dummyjson.com/recipe-images/10.webp", 
  "https://cdn.dummyjson.com/recipe-images/18.webp", 
  "https://cdn.dummyjson.com/recipe-images/24.webp", 
  "https://cdn.dummyjson.com/recipe-images/33.webp", 
  "https://cdn.dummyjson.com/recipe-images/41.webp"
];

const MealSlider = () => {
  const [imgIndex, setImgIndex] = useState(0);

  const showNextImg = () => {
    setImgIndex(imgIndex + 1 > images.length - 1 ? imgIndex + 1 : 0)
  }
  const showPrevImg = () => {
    setImgIndex(imgIndex - 1 < 0 ? images.length - 1 : imgIndex - 1)
  }

  return (
    <div style={{ maxWidth: '1200px', width: '100%', aspectRatio: '16/9', margin: "0 auto"}}>
      <div style={{width: '100%', height: '100%', position: 'relative'}}>
        <img src={images[imgIndex]} alt='food' className='img-slider-img'/>
        <button onClick={showNextImg} className='img-slider-btn' style={{left: 0}}><ArrowBigLeft /></button>
        <button onClick={showPrevImg} className='img-slider-btn' style={{right: 0}}><ArrowBigRight /></button>
      </div>
    </div>

  )
}

export default MealSlider;
