import React, {useState} from 'react'
import Recipes from '../recipes'

const LatestRecipes = () => {
  const [recipeData, setRecipeData] = useState(null);
  const [endIndex, setEndIndex] = useState(20);

  const handleRecipes = (data) => {
    setRecipeData(data.recipes);
  }

  const handleMoreRecipes = () => {
    setEndIndex(endIndex + 8 > recipeData.length - 1 ? recipeData.length : endIndex + 8)
  }

  return (
    <div>
      <Recipes endpoint={'recipes?limit=0'} onDataFetched={handleRecipes} />
      <h1 className='text-3xl font-bold font-serif ml-5'>Latest Recipes</h1>
      {recipeData && (
        <div>
          <ul className='flex flex-wrap w-full gap-4 px-4'>
            {recipeData.slice(0, endIndex).map((recipe) => 
              <li className='flexflex-1 min-w-[20%] max-w-[24%]'><img  src={recipe.image} alt={recipe.name} />{recipe.name} | {recipe.cuisine}</li>
            )}
          </ul>
          <button className='flex border-2 border-black px-2 py-1 hover:bg-gray-300 justify-self-center' onClick={handleMoreRecipes}>Load More Recipes</button>
        </div>
      )}
    </div>
  )
}

export default LatestRecipes
