import React from 'react';
import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import fb from '../img/facebook-logo.png';
import x from '../img/twitter-logo.png';
import ig from '../img/instagram-logo.png';
import search from '../img/search-icon.png';

const TopBar = () => {
	const [isExpanded, setIsExpanded] = useState(false);
	const [query, setQuery] = useState('');

	const handleSearchClick = () => {
		setIsExpanded(!isExpanded);
	};

	const handleInputChange = (e) => {
		setQuery(e.target.value);
	};

	return (
		<div className="w-screen mt-10">
			<div className="flex pt-2 pb-2 shadow-lg ">
				<a
					href="https://www.facebook.com"
					target="_blank"
					rel="noopener noreferrer"
				>
					<img src={fb} alt="Facebook" className="w-12" />
				</a>
				<a
					href="https://www.twitter.com"
					target="_blank"
					rel="noopener noreferrer"
				>
					<img src={x} alt="Twitter" className="w-12" />
				</a>
				<a
					href="https://www.instagram.com"
					target="_blank"
					rel="noopener noreferrer"
				>
					<img src={ig} alt="Instagram" className="w-12" />
				</a>
				<button onClick={handleSearchClick} className="search-icon">
					<img src={search} alt="searchbar" className="w-12 ml-10" />
				</button>
				<input
					type="text"
					className={`transition-width duration-300 ease-in-out border border-gray-300 rounded-lg outline-none ${
						isExpanded ? 'w-48 p-2 opacity-100' : 'w-0 p-0 opacity-0'
					}`}
					placeholder="Search..."
					value={query}
					onChange={handleInputChange}
					autoFocus={isExpanded}
				/>
				<button className='bg-transparent border-2 border-black rounded ml-10 px-8' >
					Login
				</button>
			</div>
		</div>
	);
};

export default TopBar;
