import React, {useState} from 'react'
import Dropdown from './Dropdown';
import search from '../img/search.svg';
import { NavLink } from 'react-router-dom';

const TopBar2 = () => {
	const [isExpanded, setIsExpanded] = useState(false);
	const [query, setQuery] = useState('');

	const handleSearchClick = () => {
		setIsExpanded(!isExpanded);
	};

	const handleInputChange = (e) => {
		setQuery(e.target.value);
	};
	return (
		<div className="flex flex-row w-full py-4 fixed top-0 z-50 bg-white space-between">
			<div className='flex-1'>
        <img src='./blessed-bites-high-resolution-logo-transparent.png' alt='logo' className='h-8' />
      </div>
      <nav className="flex flex-2 gap-5 h-full justify-center border">
        <Dropdown category={'Meals'} />
				<Dropdown category={'Cuisines'} />
				<Dropdown category={'Ingredients'} />
				<Dropdown category={'Drinks'} />
			</nav>
			<div className='flex flex-row flex-1 gap-4 justify-end pr-10 w-full'>
				<button onClick={handleSearchClick} className="search-icon">
					<img src={search} alt="searchbar" className="w- ml-10" />
				</button>
				<input
					type="text"
					className={`transition-width duration-300 ease-in-out border border-gray-300 rounded-lg outline-none ${
						isExpanded ? 'w-48 pl-2 opacity-100' : 'w-0 p-0 opacity-0'
					}`}
					placeholder="Search..."
					value={query}
					onChange={handleInputChange}
					autoFocus={isExpanded}
				/>
				<NavLink to='/profile-page' >
					<img className='w-8 rounded-full' src='./blankprofile.png' alt='profile pic'/>
				</NavLink>
			</div>
		</div>
	);
};

export default TopBar2
