import React, {useState, useReducer} from "react";

const Dropdown = ({category}) => {
    const [isOpen, setIsOpen] = useState(false);
    let options;

    const toggleMenu = () => {
      setIsOpen(!isOpen);
    }

    const closeMenu = () => {
      setIsOpen(false);
    }

    if(category === 'Meals') {
      options = (
        <ul>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
        </ul>
      )
    } else if(category === 'Cusines') {
      options = (
        <ul>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
        </ul>
      )
    } else if(category === 'Difficulty') {
      options = (
        <ul>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
        </ul>
      )
    } else if (category === 'Drinks') {
      options = (
        <ul>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
          <li onClick={closeMenu}></li>
        </ul>
      )
    }

    return (
      <div className="hover:bg-gray-100">
        <button onClick={toggleMenu} className="dropdown-button">
          {category}
        </button>
        {isOpen && (
          <div className="dropdown-content bg-white">
            {options}
          </div>
        )}
    </div>
  );
}

export default Dropdown;
