		setApiData(data)
		if(apiData) {
			console.log(apiData?.recipes)
		}
	}

	return (
		<div>
			<Recipes endpoint={'recipes?limit=0'} type={'image'} onDataFetched={handleDataFromChild} />
			<ul>
				{apiData?.recipes.map(item => 
					<li key={item.id}>{item.name} {item.id}<img src={item.image} alt='food' /></li>
				)}
			</ul>
		</div>
	);
};

export default App;
