import React, { useState } from 'react';
import {ArrowBigLeft, ArrowBigRight} from 'lucide-react';
import '../styles/img-slider.css'

const images = ["https://cdn.dummyjson.com/recipe-images/10.webp", "https://cdn.dummyjson.com/recipe-images/18.webp", "https://cdn.dummyjson.com/recipe-images/33.webp", "https://cdn.dummyjson.com/recipe-images/42.webp", "https://cdn.dummyjson.com/recipe-images/43.webp"];

const MealSlider = () => {
  const [imgIndex, setImgIndex] = useState(4);

  return (
    <div style={{ maxWidth: '1000px', width: '100%', aspectRatio: '16/9', margin: "0 auto"}}>
      <div style={{width: '100%', height: '100%'}}>
        <img src={images[imgIndex]} alt='food' className='img-slider-img'/>
        <button><ArrowBigLeft /></button>
        <button><ArrowBigRight /></button>
      </div>
    </div>

  )
}

export default MealSlider;
