import React, {useState} from "react";

const Dropdown = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    }

    const closeMenu = () => {
        setIsOpen(false);
    }

    return (
      <div className="dropdown">
        <button onClick={toggleMenu} className="dropdown-button">
          Menu
        </button>
        {isOpen && (
          <div className="dropdown-content">
            <ul>
              <li onClick={closeMenu}>Option 1</li>
              <li onClick={closeMenu}>Option 2</li>
              <li onClick={closeMenu}>Option 3</li>
            </ul>
          </div>
        )}
    </div>
  );
}

export default Dropdown;
