import React, {useState, useReducer} from "react";

const categories = {
  meals: 'Meals',
  cuisines: 'Cuisines',
  difficulty: 'Difficulty',
  drinks: 'Drinks'
}

const reducer = (state, action) => {
  switch (action.type) {
    case categories.meals: 
      return '';
    case categories.cuisines: 
      return '';
    case categories.difficulty: 
      return '';
    case categories.drinks: 
      return '';
    default:
      return state;

  }
}

const Dropdown = ({meal}) => {
    const [isOpen, setIsOpen] = useState(false);
    let options;

    const toggleMenu = () => {
      setIsOpen(!isOpen);
    }

    const closeMenu = () => {
      setIsOpen(false);
    }

    // if(meal === 'Meals') {
    //   options = {
    //     <li> onClick=</li>
    //   }
    // }

    return (
      <div className="dropdown">
        <button onClick={toggleMenu} className="dropdown-button">
          {meal}
        </button>
        {isOpen && (
          <div className="dropdown-content bg-gray-200">
            <ul>
              <li onClick={closeMenu} className="hover:bg-white">Option 1</li>
              <li onClick={closeMenu} className="hover:bg-white">Option 2</li>
              <li onClick={closeMenu} className="hover:bg-white">Option 3</li>
            </ul>
          </div>
        )}
    </div>
  );
}

export default Dropdown;
