import React, { useEffect, useState } from 'react';

const RecipesAPI = ({ endpoint, query }) => {
	const [data, setData] = useState([]); // State to store fetched data
	const [loading, setLoading] = useState(true); // State to show loading status
	const [error, setError] = useState(null); // State to show errors

	useEffect(() => {
    // Reset state when the endpoint or query changes
    setLoading(true);
    setError(null);

    const fetchData = async () => {
      try {
        // Construct the URL based on the passed props
        const url = query 
          ? `https://api.example.com/${endpoint}?q=${query}`
          : `https://api.example.com/${endpoint}`;

        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`Network response was not ok: ${response.statusText}`);
        }

        const result = await response.json();
        setData(result);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [endpoint, query]);

	useEffect(() => {
		setLoading(true);
    setError(null);
		// Fetch data when component mounts
		fetch('https://dummyjson.com/recipes?limit=0') // Example API endpoint
			.then((res) => {
				if (!res.ok) {
					throw new Error('Network response was not ok');
				}
				return res.json();
			})
			.then((data) => {
				console.log(
					// data.recipes.filter((recipe) => recipe.mealType.includes('Breakfast'))
					data.recipes
				);
				setData(data.recipes)
				// setData(data.recipes.filter(recipe => recipe.id === 10 || recipe.id === 35 || recipe.id === 47)); // Update data state
				setLoading(false); // Set loading to false after data is fetched
			})
			.catch((error) => {
				setError(error); // Set error state if there’s an error
				setLoading(false);
			});
	}, [endpoint, query]);

	if (loading) return <p>Loading...</p>;
	if (error) return <p>Error: {error.message}</p>;

	return (
		<div>
			<h1>Fetched Data</h1>
			<ul>
				{data.map((item) => (
					<li key={item.id}>
						{item.name} {item.id-1}
						<img className="w-60" src={item.image} alt={item.name} />
					</li>
				))}
			</ul>
		</div>
	);
};

export default Recipes;

